mapper.select:
select m.nick, b.*
from member m join board b
on m.userid=b.userid
where category=#{category}
order by num desc;

select m.nick, b.*
from member m join board b
on m.userid=b.userid
where b.num=#{num}
order by num desc;

mapper.update:
update board
set	category=#{category}, subject=#{subject}, content=#{content}
where num=#{num} and userid=#{userid};
